import React, { useState } from 'react';
import "./Chat.css"

function Chat() {
    const [input, setInput] = useState("");
    const sendMessage = (e) => {
        e.preventDefault();
        //firebase magic
    };
    return (
        <div className="chat">

            {/* chat hearder */}
            <div className="chat__header">
                <h4>To: <span className="chat__name">Channel name</span></h4>
                <strong>Details</strong>
            </div>
            {/* chat message */}

            {/* chat input */}
            <div className="chat__input">
                <form>
                    <input value={input} onChange={(e) => setInput(e.ta)}  placeholder="iMessage" type="text" />
                        <button onClick={sendMessage}>Send Message</button>
                </form>
            </div>
        </div>
    )
}

export default Chat
